bind.calculate:
if ( /assessment/group_cough/patient_coughs ='yes',
 concat (
 'Cough',
 if( string-length( /assessment/group_cough/coughing_duration ) != 0,
 concat (
 ' for ',
 jr:choice-name( /assessment/group_cough/coughing_duration , ' /assessment/group_cough/coughing_duration ')
 ),
 ''
 )
 ),
 ''
)

if ( /assessment/group_diarrhea/patient_diarrhea ='yes',
 concat(
 'Diarrhoea',
 if( string-length( /assessment/group_diarrhea/diarrhea_duration ) != 0,
 concat (
 ' for ',
 jr:choice-name( /assessment/group_diarrhea/diarrhea_duration , ' /assessment/group_diarrhea/diarrhea_duration ')
 ),
 ''
 ),
 if ( /assessment/group_diarrhea/diarrhea_blood ='yes', ' with blood', '')
 ),
 ''
)

if ( /assessment/group_fever/patient_fever ='yes' or  /assessment/group_fever/patient_temperature  > 37.5,
 concat (
 if( /assessment/group_fever/patient_temperature  >= 40,
 'Severe fever',
 'Fever'
 ),
 if( string-length( /assessment/group_fever/fever_duration ) != 0,
 concat (
 ' for ',
 jr:choice-name( /assessment/group_fever/fever_duration , ' /assessment/group_fever/fever_duration ')
 ),
 ''
 )
 ),
 ''
)

if( string-length( /assessment/group_fever/mrdt_result ) != 0,
 concat (
 'Malaria: ',
 jr:choice-name( /assessment/group_fever/mrdt_result , ' /assessment/group_fever/mrdt_result ')
 ),
 ''
)

if(( /assessment/group_breathing/breath_count >=60) and ( /assessment/patient_age_in_days <60), "true",
 if(( /assessment/group_breathing/breath_count >=50) and ( /assessment/patient_age_in_days >=60) and ( /assessment/patient_age_in_days <365), "true",
 if(( /assessment/group_breathing/breath_count >=40) and ( /assessment/patient_age_in_days >=365) and ( /assessment/patient_age_in_days <(5*365.25)), "true",
 if(( /assessment/group_breathing/breath_count >=30) and ( /assessment/patient_age_in_days >=(5*365.25)), "true", "false" ))))

if ( /assessment/group_cough/patient_coughs ='yes',
 if ( int( /assessment/group_cough/coughing_duration ) > 14,
 if (  /assessment/group_cough/chest_indrawing ='yes' or  /assessment/group_breathing/fast_breathing ='true',
 'pneumonia2c',
 'cough2'
 ),
 if (  /assessment/group_cough/chest_indrawing ='yes' and  /assessment/group_breathing/fast_breathing ='true',
 'pneumonia2b',
 if (  /assessment/group_cough/chest_indrawing ='yes',
 'cough3',
 if (  /assessment/group_breathing/fast_breathing ='true',
 'pneumonia1',
 'cough1'
 )
 )
 )
 ),
 ''
)

if ( /assessment/group_diarrhea/patient_diarrhea ='yes',
 if ( int( /assessment/group_diarrhea/diarrhea_duration ) > 14 or  /assessment/group_diarrhea/diarrhea_blood  = 'yes',
 'diarrhea2',
 'diarrhea1'
 ),
 ''
)

if ( /assessment/group_fever/patient_fever ='yes' or  /assessment/group_fever/patient_temperature  > 37.5,
 if (  /assessment/group_fever/mrdt_result  = 'negative',
 if ( int( /assessment/group_fever/fever_duration ) > 7 or  /assessment/group_fever/patient_temperature  >= 40,
 'fever2',
 'fever1'
 ),
 if ( int( /assessment/group_fever/fever_duration ) > 7 or  /assessment/group_fever/patient_temperature  >= 40,
 'malaria2',
 'malaria1'
 )
 ),
 ''
)

if ( /assessment/referral_follow_up  = 'true',
 'Follow up in 1 day to ensure that patient goes to a health facility',
 if ( /assessment/treatment_follow_up  = 'true',
 'Follow up in 2 days to see if there is improvement with treatment',
 ''
 )
)
